import React, { useState, useEffect } from "react"
import { View, StyleSheet, Text, Button } from "react-native"
import { SafeAreaProvider } from "react-native-safe-area-context"
import { BarCodeScanner } from "expo-barcode-scanner"

const HomeScreen = ({ navigation }) => {
  const [hasPermission, setHasPermission] = useState(null)
  const [scanned, setScanned] = useState(false)
  const [product, setProduct] = useState({})
  const [result, setResult] = useState(null)

  // check for camera permissions
  useEffect(() => {
    (async () => {
      const { status } = await BarCodeScanner.requestPermissionsAsync()
      setHasPermission(status === "granted")
    })()
  }, [])

  useEffect(() => {

    (async () => {
          console.log("scan result", result)
      if(result){
        const productData = await fetchNutrition(result)
        setProduct(productData)
        setScanned(false)
      }      
    })()
  }, [result])


  const handleBarCodeScanned = async ({ type, data }) => {
    setScanned(true)
    console.log('scanned', data)
    setResult(data)
    const productData = await fetchNutrition(data)

    // if(productData){
    //   setProduct(productData)
    //   console.log('got product data')
    //   console.log(product)
    // }    

  }
  const fetchNutrition = async (upc) => {
    try {
      const response = await fetch(
        `https://world.openfoodfacts.org/api/v0/product/${upc}.json`
      )
      const data = await response.json()

      return data
    } catch (error) {
      console.log("error")
    }
  }

  // Check for device permission
  if (hasPermission === null) {
    return <Text>Requesting for camera permission</Text>
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>
  }


  const productImage = product?.product?.image_url
  const productName = product?.product?.product_name

  return (
    <SafeAreaProvider>
      <View style={{ flex: 1, justifyContent: "start", alignItems: "center" }}>
        <Text> {productName}</Text>
        <BarCodeScanner
          onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
          style={{
            // ...StyleSheet.absoluteFillObject,
            // position: "relative",
            // bottom: "50%",
            left: 0,
            right: 0,
            width: "95%",
            borderRadius: "12px",
            overflow: "hidden",
            height: 300,
            top: 60,
          }}
        />
      </View>
    </SafeAreaProvider>

  )

}

export default HomeScreen
